mapper.select:
SELECT COUNT(*)
FROM APPROVAL
WHERE
	(
	FIRST_APPROVER = #{employeeNo}
	OR SECOND_APPROVER = #{employeeNo}
	OR THIRD_APPROVER = #{employeeNo}
	OR FINAL_APPROVER = #{employeeNo}
	)
	AND STATUS = '대기'

mapper.update:
UPDATE APPROVAL
SET
    FINAL_DATE = SYSDATE
    ,STATUS = '반려'
    ,RETURN_REASON = #{returnReason}
 WHERE "NO" = #{no}

mapper.insert:
INSERT INTO AP_DOC
 (
     NO
    ,WRITER_NO
    ,TITLE
    ,"DATE"
    ,"TYPE"  
 )
 VALUES
 (
    SEQ_DOC_NO.NEXTVAL
    ,#{writerNo}
    ,#{title}
    ,SYSDATE
    ,#{type}
 )

INSERT INTO APPROVAL
 (
     NO
    ,DOC_NO
    ,FIRST_APPROVER
    ,SECOND_APPROVER
    ,THIRD_APPROVER
    ,FINAL_APPROVER
    ,STATUS
 )
 VALUES
 (
    SEQ_APP_NO.NEXTVAL
    ,SEQ_DOC_NO.CURRVAL
    ,#{firstApprover}
    ,#{secondApprover}
    ,#{thirdApprover}
    ,#{finalApprover}
    ,'대기'
 )

INSERT INTO DRAFT
(
    NO
    ,DEPARTMENT
    ,"CONTENT"
)
VALUES
(
    SEQ_DOC_NO.CURRVAL
    ,#{department}
    ,#{content}
)
