mapper.select:
		SELECT COUNT(*)
      	FROM COMMUTE
      	WHERE EMP_NO = #{empNo}

SELECT L."BEGIN" ,L."END" ,L."APPLY" FROM LEAVE L JOIN AP_DOC D ON L.NO = D.NO JOIN APPROVAL A ON A.DOC_NO = D.NO WHERE D.WRITER_NO = #{memberNo} AND A.STATUS = '승인' AND (
TO_DATE(L."BEGIN") >= TO_DATE('${year}-01-01') OR TO_DATE(L."END") <= TO_DATE('${year}-12-31')
)
