mapper.update:
  	UPDATE
		EMPLOYEE
		SET
			  DEPT_NO = #{deptNo}
			, POS_NO = #{posNo}
			, EMAIL = #{email}
			, PHONE = #{phone}
		WHERE NO = #{no}

UPDATE
	ACCOUNT
