		  NAME = #{name}
		, ADDRESS = #{address}
		, TEL = #{tel}
		, FAX = #{fax}
		, PNAME = #{pname}
		, DEPT = #{dept}
		, POSITION = #{position}
		, PHONE = #{phone}
		, EMAIL = #{email}
	WHERE NO = #{no}
